<?phpnamespace Home\Common;use Think\Controller;//Home模块父类class HomeController extends Controller {    //控制器类公共函数    public function _initialize() {        $user['id'] = session('ey_id');        $user['user'] = session('ey_user');        $this->user = $user;        $this->seo();    }    //系统日志    public function log($str, $status) {        $data = '[' . date("Y/m/d H:i:s") . ']';        $data.='----' . $status;        $data.='----' . $_SERVER["REMOTE_ADDR"];        $data.='----HOME***' . $str . "***";        if (strtoupper(substr(PHP_OS, 0, 3)) === 'WIN') {//判断服务器类型            $data.="\r\n";        } else {            $data.="\n";        }        $myfile = fopen("./log.txt", "a") or die("Unable to open file!"); //a表示追加写入，w表示覆盖写入        fwrite($myfile, $data);        fclose($myfile);    }    //SEO信息    public function seo() {        $config = C('web_config');        $config['web_f_url'] = htmlspecialchars_decode($config['web_f_url']);        $config['web_footer'] = htmlspecialchars_decode($config['web_footer']);        $this->seo = $config;    }}